# reverse version of the above
_COMBO_HAND_MAP = { combo: {hand for hand, combo_set in _HAND_COMBO_MAP.items() if combo in combo_set}
                        for combo in _HAND_COMBOS   }


class HandComboMap:
    HAND_COMBOS  = _HAND_COMBOS
    PREFLOP_HANDS = _PREFLOP_HANDS
    HAND_COMBO_MAP = _HAND_COMBO_MAP
    COMBO_HAND_MAP = _COMBO_HAND_MAP

    @classmethod
    def gen_all_combos(cls):
        yield from cls.HAND_COMBOS

    @classmethod
    def gen_preflop_hands(cls):
        yield from cls.PREFLOP_HANDS

    @classmethod
    def normalize_combo(cls, combo: str):
        if combo in cls.HAND_COMBOS:
            return combo
        else:
            combo = combo[2:4] + combo[0:2]
            if combo in cls.HAND_COMBOS:
                return combo
            raise ValueError(f"Invalid combo `{combo}`")

    @classmethod
    def normalize_hand(cls, hand: str):
        try:
            return cls.normalize_combo(hand)
        except ValueError:
            pass            
        # otherwise
        if hand in cls.HAND_COMBO_MAP:
            return hand
        else:
            hand = hand[1] + hand[0] + hand[2:]
            if hand in cls.HAND_COMBO_MAP:
                return hand
            raise ValueError(f"Invalid hand `{hand}`")


    @classmethod
    def gen_combos_for_hand(cls, hand: str):
        hand = cls.normalize_hand(hand)
        if hand in cls.HAND_COMBOS:
            yield hand
        else:
            yield from cls.HAND_COMBO_MAP[hand]

    @classmethod
    def num_combos_for_hand(cls, hand: str):
        hand = cls.normalize_hand(hand)
        if hand in cls.HAND_COMBOS:
            return 1
        return len(cls.HAND_COMBO_MAP[hand])

    @classmethod
    def gen_hands_for_combo(cls, combo: str):
        yield from cls.COMBO_HAND_MAP[cls.normalize_combo(combo)]


    @classmethod
    def preflop_hand_for_combo(cls, combo: str):
        try:
            return next(h for h in cls.gen_hands_for_combo(combo) if h in cls.PREFLOP_HANDS)
        except StopIteration:
            raise ValueError(f"Could not find preflop hand for combo `{combo}` !")
